import React from "react";
import {
  CommentDiv,
  DetailComment,
  DetailContainer,
  DetailBanner,
  ImageBox,
  ImageText,
  ImgDiv,
  DetailTitle,
  CommentListDiv,
} from "./style";
import Comment from "../comment/Comment";

const comments = [
  { id: 1, user: "User1", content: "Great post!", date: "2023-07-15" },
  { id: 2, user: "User2", content: "Thanks for sharing.", date: "2023-07-16" },
];

// import { useDispatch, useSelector } from "react-redux";
// import { deleteComment, getComments } from "../../redux/commentsSlice";

const ContainerForm = () => {
  // const comments = useSelector((state) => state.comments);
  // const dispatch = useDispatch();

  // useEffect(() => {
  //   dispatch(getComments());
  // }, [dispatch]);

  // const handlerDeleteComment = (id) => {
  //   dispatch(deleteComment(id));
  // };

  return (
    <>
      <DetailContainer>
        <DetailBanner>Oh! Honey Scrooge</DetailBanner>
        <DetailTitle>상세 페이지</DetailTitle>
        <ImgDiv>
          <ImageBox>
            <p>꿀템 사진</p>
          </ImageBox>
          <ImageText>
            <p>꿀템 설명</p>
          </ImageText>
        </ImgDiv>
        <CommentDiv>
          <DetailComment>
            <div id="comment-count">댓글 숫자 0 </div>
            <input id="comment-input" placeholder="댓글을 입력하세요"></input>
            <button>확인</button>
          </DetailComment>
          <CommentListDiv id="comments">
            {comments.map((comment) => (
              <Comment
                key={comment.id}
                comment={comment}
                // onDelete={handlerDeleteComment} // 주석 처리된 코드를 나중에 사용하시려면 주석을 해제하세요.
              />
            ))}
          </CommentListDiv>
        </CommentDiv>
      </DetailContainer>
    </>
  );
};

export default ContainerForm;
