import React, { useEffect, useState } from "react";
import axios from "axios";
import ReactDOM from "react-dom";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import useInput from "../components/input/useInput";

const Logins = styled.div`
  width: 1440px;
  height: 60%;
  margin: 0 auto;
  .membersLogin {
    display: flex;
    height: 100%;
  }
  .loginMessage {
    height: 420px;
    width: 50%;
    background: #090909;
    border: 2px solid #ff0090;
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .loginMessage > h4 {
    color: #ffffff;
    font-size: 40px;
    text-align: center;
  }
  .membersLogin > form {
    display: flex;
    flex-direction: column;
    width: 50%;
    height: 420px;
    border: 2px solid #ff0090;
    padding: 1%;
  }
  .membersLogin > form > h4 {
    font-size: 40px;
    text-align: center;
    justify-content: center;
    border-bottom: 1px solid #ff0090;
  }
  .loginForm {
    display: flex;
    flex-direction: column;
    width: 100%;
    height: 20%;
    margin-top: 25px;
  }
  .loginForm > .id {
    display: flex;
  }
  .loginForm > .id > input {
    width: 300px;
  }
  .loginForm > .id > button {
    width: 100px;
  }
  .loginForm > .password {
    display: flex;
    flex-direction: column;
  }
`;

const Login = () => {
  const [inputId, setInputId] = useInput("");
  const [inputPw, setInputPw] = useInput("");
  const [login, setLogin] = useState(false);
  const [data, setData] = useState([]);
  const navigate = useNavigate();

  const handleInputId = (e) => {
    setInputId(e.target.value);
  };
  const handleInputPw = (e) => {
    setInputPw(e.target.value);
  };
  const onClickLogin = () => {
    axios
      .post("https://honeyitem.shop/api/login", {
        userId: inputId,
        password: inputPw,
      })
      .then((response) => {
        if (response.data.success) {
          // 로그인 성공
          setLogin(true);
          navigate("/"); // 로그인 성공 시 메인 페이지 리다이렉트
        } else {
          // 로그인 실패
          setLogin(false);
          alert(response.data.message); // 서버에서 받은 실패 메시지를 알림
        }
      })
      .catch((error) => {
        // 서버 에러
        console.error(error);
      });
  };
  useEffect(() => {
    axios
      .post("https://honeyitem.shop/api/login")
      .then((res) => console.log(res))
      .catch();
  }, []);

  return (
    <Logins>
      <div className='membersLogin'>
        <div className='loginMessage'>
          <h4>O.H.S에 방문해주셔서 감사합니다.</h4>
        </div>
        <div>
          <h4>Login</h4>
          <div className='loginForm'>
            <div className='id'>
              <h5>아이디:</h5>
              <input
                type='text'
                placeholder='아이디'
                value={inputId}
                onChange={handleInputId}
              />
            </div>
            <div className='password'>
              <h5>비밀번호:</h5>
              <input
                type='text'
                placeholder='비밀번호를 입력해주세요'
                value={inputPw}
                onChange={handleInputPw}
              />
            </div>
          </div>
          <button onClick={onClickLogin}>로그인</button>
        </div>
      </div>
    </Logins>
  );
};

export default Login;
