import React from "react";
import {
  CommentDiv,
  DetailComment,
  DetailContainer,
  DetailBanner,
  ImageBox,
  ImageText,
  ImgDiv,
  DetailTitle,
  CommentListDiv,
} from "./style"; // 스타일 관련 컴포넌트를 import

// 댓글 데이터를 임의로 생성
const comments = [
  { id: 1, user: "User1", content: "Great post!", date: "2023-07-15" },
  { id: 2, user: "User2", content: "Thanks for sharing.", date: "2023-07-16" },
];

// Redux 관련 코드가 주석처리 되어 있습니다.
// const dispatch = useDispatch();
// const comments = useSelector((state) => state.comments);
// useEffect(() => {
//   dispatch(getComments());
// }, [dispatch]);

// 주석처리된 댓글 삭제 핸들러 함수
// const handlerDeleteComment = (id) => {
//   dispatch(deleteComment(id));
// };

const ContainerForm = () => {
  return (
    <>
      <DetailContainer>
        {" "}
        // 상세페이지의 전체적인 컨테이너
        <DetailBanner>Oh! Honey Scrooge</DetailBanner> // 배너 제목
        <DetailTitle>상세 페이지</DetailTitle> // 상세페이지 제목
        <ImgDiv>
          {" "}
          // 이미지 및 설명을 담는 div
          <ImageBox>
            <p>꿀템 사진</p>
          </ImageBox>
          <ImageText>
            <p>꿀템 설명</p>
          </ImageText>
        </ImgDiv>
        <CommentDiv>
          {" "}
          // 댓글을 담는 div
          <DetailComment>
            {" "}
            // 댓글 작성 관련 부분
            <div id="comment-count">댓글 숫자 0 </div> // 댓글 수를 보여주는 div
            <input id="comment-input" placeholder="댓글을 입력하세요"></input> // 댓글 작성을 위한 input field
            <button>확인</button> // 댓글 작성을 위한 버튼
          </DetailComment>
          <CommentListDiv>
            {" "}
            // 댓글 목록을 담는 div
            <div id="comments">
              {comments.map((comment) => (
                <div className="comment" key={comment.id}>
                  {" "}
                  // 각 댓글을 렌더링하는 부분
                  <div className="comment-user">
                    <p>{comment.user}</p>
                  </div>
                  <div className="comment-content">
                    <p>{comment.content}</p>
                  </div>
                  <div className="comment-date">
                    <p>{comment.date}</p>
                  </div>
                  <button className="comment-edit">Edit</button> // 댓글 수정 버튼
                  <button
                    className="comment-delete"
                    // onClick={() => handlerDeleteComment(comment.id)} // 댓글 삭제 버튼. 주석처리된 함수를 사용하려면 주석을 해제하세요.
                    onClick={() => console.log(`Delete comment with id ${comment.id}`)}
                  >
                    Delete
                  </button>
                </div>
              ))}
              ;
            </div>
          </CommentListDiv>
        </CommentDiv>
      </DetailContainer>
    </>
  );
};

export default ContainerForm; // ContainerForm 컴포넌트를 export
