import React from "react";
import {
  CommentDiv,
  DetailComment,
  DetailContainer,
  DetailBanner,
  ImageBox,
  ImageText,
  ImgDiv,
  DetailTitle,
  CommentListDiv,
} from "./style";

const comments = [
  { id: 1, user: "User1", content: "Great post!", date: "2023-07-15" },
  { id: 2, user: "User2", content: "Thanks for sharing.", date: "2023-07-16" },
];
// import { useDispatch, useSelector } from "react-redux";
// import { deleteComment, getComments } from "../../redux/commentsSlice";

const ContainerForm = () => {
  // const comments = useSelector((state) => state.comments);
  // const dispatch = useDispatch();

  // useEffect(() => {
  //   dispatch(getComments());
  // }, [dispatch]);

  // const handlerDeleteComment = (id) => {
  //   dispatch(deleteComment(id));
  // };

  return (
    <>
      <div className="Container">
        <DetailContainer>
          <DetailBanner>Oh! Honey Scrooge</DetailBanner>
          <DetailTitle>상세 페이지</DetailTitle>
          <ImgDiv>
            <ImageBox>
              <p>꿀템 사진</p>
            </ImageBox>
            <ImageText>
              <p>꿀템 설명</p>
            </ImageText>
          </ImgDiv>
        </DetailContainer>
        <CommentDiv>
          <DetailComment>
            <div id="comment-count">댓글 숫자 0 </div>
            <input id="comment-input" placeholder="댓글을 입력하세요"></input>
            <button>확인</button>
          </DetailComment>
          <CommentListDiv>
            <div id="comments">
              {comments.map((comment) => (
                <div className="comment" key={comment.id}>
                  <div className="btnBox">
                    <button className="comment-edit">Edit</button>
                    <button
                      className="comment-delete"
                      // onClick={() => handlerDeleteComment(comment.id)}
                      onClick={() => console.log(`Delete comment with id ${comment.id}`)}
                    >
                      Delete
                    </button>
                  </div>

                  <div className="comment-user">
                    <p>{comment.user}</p>
                  </div>

                  <div className="comment-content">
                    <p>{comment.content}</p>
                  </div>

                  <div className="comment-date">
                    <p>{comment.date}</p>
                  </div>
                </div>
              ))}
              ;
            </div>
          </CommentListDiv>
        </CommentDiv>
      </div>
    </>
  );
};

export default ContainerForm;
