import { styled } from "styled-components";

export const CommentListDiv = styled.div`
  margin-top: 100px;
  border: 2px solid #FF0090;
  width: 1000px;
  border-radius: 40px;
  box-shadow: 6px 6px 6px rgba(0, 0, 0, 0.4);

  comment-box {
  margin-bottom: 10px;
  padding: 10px;
  background-color: #f0f0f0;
  border: 1px solid #ccc;
  border-radius: 8px;
}

.comment-user {
  font-weight: bold;
}

.comment-content {
  margin-top: 5px;
}

.comment-date {
  font-size: 12px;
  color: #888;
}

.comment-edit,
.comment-delete {
  gap: 10px;
  background-color: #FF0090;
  border: 0px;
  border-radius: 20px;
  width: 50px;
  height: 30px;
}
`;

export const CommentBox = styled.div`
width: 1000px;
margin: auto;
`
