    <>
      <div className="Container">
        <DetailContainer>
          <DetailBanner>Oh! Honey Scrooge</DetailBanner>
          <DetailTitle>상세 페이지</DetailTitle>

          <ImgDiv>
            <ImageBox>
              <p>꿀템 사진</p>
            </ImageBox>
            <ImageText>
              <p>꿀템 설명</p>
            </ImageText>
          </ImgDiv>

          <Comment comments={comments} onDelete={handleDelete} />
        </DetailContainer>
      </div>
    </>
  );
};

export default ContainerForm;
