import React, { useState } from "react";
import axios from "axios";
import { styled } from "styled-components";
import { Form, useNavigate } from "react-router-dom";

const Joins = styled.div`
  width: 1440px;
  height: 60%;
  margin: 0 auto;
  .membersJoin {
    display: flex;
    height: 100%;
  }
  .login {
    height: 420px;
    width: 50%;
    background: #090909;
    border: 2px solid #ff0090;
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .login > h4 {
    color: #ffffff;
    font-size: 25px;
    text-align: center;
  }
  .membersJoin > form {
    display: flex;
    flex-direction: column;
  }
  .joinForm {
    display: flex;
    flex-direction: column;
    width: 100%;
  }
  .joinForm > .id {
    display: flex;
  }
  .joinForm > .id > input {
    width: 300px;
  }
  .joinForm > .id > button {
    width: 100px;
  }
`;

const Join = () => {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const [nickname, setNickname] = useState("");
  const [isIdDuplicated, setIsIdDuplicated] = useState(false);
  const [login, setLogin] = useState(false);
  const navigate = useNavigate();
  const loginHandler = () => {
    axios.post("http://3.38.191.164/", {});
  };

  const membersJoin = () => {
    alert("회원가입이 완료되었습니다");
    navigate("/login");
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isIdDuplicated || isNicknameDuplicated) {
      alert("중복된 아이디 또는 별명입니다.");
    } else {
      membersJoin();
    }
  };

  const handleIdCheck = async () => {
    try {
      const response = await axios.get(`/api/checkIdDuplicate?id=${id}`);
      const isDuplicate = response.data.isDuplicate;
      setIsIdDuplicated(isDuplicate);
      if (isDuplicate) {
        alert("중복된 아이디입니다.");
      }
    } catch (error) {
      console.error("아이디중복이 확인되지 않습니다:", error);
    }
  };

  return (
    <Joins>
      <div className='membersJoin'>
        <div className='login'>
          <h4>O.H.S에 오신 걸 환영합니다</h4>
        </div>
        <form onsubmit={handleSubmit}>
          <div className='joinForm'>
            <div className='id'>
              <input
                type='text'
                placeholder='아이디'
                value={id}
                onChange={(e) => setId(e.target.value)}
              />
              <button onClick={handleIdCheck}>중복확인</button>
            </div>
            {isIdDuplicated && <span>중복된 아이디입니다.</span>}
            <div className='nickname'>
              <input
                type='text'
                placeholder='별명을 입력하세요'
                value={nickname}
                onChange={(e) => setNickname(e.target.value)}
              />
            </div>
            <input
              type='password'
              placeholder='비번'
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button onClick={loginHandler}>회원가입</button>
        </form>
      </div>
    </Joins>
  );
};

export default Join;
