import { styled } from "styled-components";

export const CommentForm = styled.div`

#comment-count {
  margin-bottom: 1vh;
  font-size: 20px;
}

#comment-input {
  width: 60%;
  height: 40px;
  font-size: 15px;
  border: 2px solid #ff0090;
  cursor: pointer;
  border-radius: 20px;
}

button {
  width: 100px;
  height: 47px;
  margin-left: 2vh;
  border: 2px solid #ff0090;
  background-color: white;
  border-radius: 20px;
  cursor: pointer;

}
`;
