import { styled } from "styled-components";

export const DetailComment = styled.div`

#comment-count {
  margin-bottom: 1vh;
  font-size: 20px;
}

#comment-input {
  width: 60%;
  height: 40px;
  font-size: 15px;
  border: 2px solid #ff0090;
  cursor: pointer;
  border-radius: 20px;
  box-shadow: 6px 6px 6px rgba(0, 0, 0, 0.4)
}

button {
  width: 100px;
  height: 47px;
  margin-left: 2vh;
  border: 0px;
  background-color: #ff0090;
  color: white;
  border-radius: 20px;
  cursor: pointer;
  box-shadow: 6px 6px 6px rgba(0, 0, 0, 0.4);
  &:hover {
    scale(1.2);
  }
}

#comment-count{
  float: left;

}
`;
