import { styled } from "styled-components";

export const DetailComment = styled.div`

#comment-count {
  margin-bottom: 1vh;
  font-size: 20px;
}

#comment-input {
  width: 60%;
  height: 40px;
  font-size: 15px;
  border: 2px solid #ff0090;
  cursor: pointer;
  border-radius: 20px;
  box-shadow: 6px 6px 6px rgba(0, 0, 0, 0.4);

}

button {
  width: 100px;
  height: 47px;
  margin-left: 2vh;
  border: 0px;
  background-color: #ff0090;
  color: white;
  border-radius: 20px;
  cursor: pointer;
  box-shadow: 6px 6px 6px rgba(0, 0, 0, 0.4);
  &:hover {
    transform: scale(1.1);
    animation: ease-in;
  }

  .input-wrapper {
  position: relative;
}

.input-wrapper label {
  position: absolute;
  top: 0;
  left: 0;
  margin-left: 8px; /* 띄어쓰기 조절 */
  pointer-events: none;
  transition: all 0.3s ease;
}

.input-wrapper input:focus + label,
.input-wrapper input:not(:placeholder-shown) + label {
  transform: translateY(-30px);
  font-size: 12px;
  color: #888;
}
}

`;
