import React from "react";
import CommentForm from "../comment/CommentForm";
import CommentList from "../list/CommentList";
import { CommentDiv, DetailContainer, DetailBanner, ImageBox, ImageText, ImgDiv, DetailTitle } from "./style";
import Comment from "./comment/Comment";

const comments = [
  { id: 1, user: "User1", content: "Great post!", date: "2023-07-15" },
  { id: 2, user: "User2", content: "Thanks for sharing.", date: "2023-07-16" },
];
// import { useDispatch, useSelector } from "react-redux";
// import { deleteComment, getComments } from "../../redux/commentsSlice";

const ContainerForm = () => {
  // const comments = useSelector((state) => state.comments);
  // const dispatch = useDispatch();

  // useEffect(() => {
  //   dispatch(getComments());
  // }, [dispatch]);

  // const handlerDeleteComment = (id) => {
  //   dispatch(deleteComment(id));
  // };

  const ContainerForm = () => {
    const handleConfirm = () => {
      console.log("Confirmed!");
    };

    const handleDelete = (id) => {
      console.log(`Delete comment with id ${id}`);
    };

    return (
      <>
        <div className="Container">
          <DetailContainer>
            <DetailBanner>Oh! Honey Scrooge</DetailBanner>
            <DetailTitle>상세 페이지</DetailTitle>

            <ImgDiv>
              <ImageBox>
                <p>꿀템 사진</p>
              </ImageBox>
              <ImageText>
                <p>꿀템 설명</p>
              </ImageText>
            </ImgDiv>
          </DetailContainer>
          <CommentDiv>
            <CommentForm onConfirm={handleConfirm} />
            <CommentList comments={comments} onDelete={handleDelete} />
          </CommentDiv>
        </div>
      </>
    );
  };
};

export default ContainerForm;
