import { styled } from "styled-components";

export const CommentListDiv = styled.div`
  margin-top: 100px;
  border: 2px solid #FF0090;
  width: 1000px;
  border-radius: 40px;
  box-shadow: 6px 6px 6px rgba(0, 0, 0, 0.4);

  .comments {
    width: 1000px;
    height: 150px;
    background-color: #f0f0f0;
  }

  .comment-box {
  margin-bottom: 10px;
  padding: 10px;
  background-color: #f0f0f0;
  border: 1px solid #ccc;
  border-radius: 8px;
}

.comment-user {
  font-size: 18px;
  font-weight: bolder;
}

.comment-content {
  font-weight: bold;
  margin-top: 5px;
}

.comment-date {
  font-size: 14px;
  color: #888;
}

.comment-edit,
.comment-delete {
  margin-right: 5px;
  background-color: #FF0090;
  border: 0px;
  border-radius: 20px;
  width: 50px;
  height: 30px;
  cursor: pointer;
  margin-bottom: 18px;
  &:hover {
    transform: scale(1.1);
    animation: ease-in;
  }
}
`;

export const CommentBox = styled.div`
width: 1000px;
margin: auto;
padding-bottom: 50px;
`
