import React, { useEffect } from "react";
import {
  CommentDiv,
  DetailComment,
  DetailContainer,
  DetailBanner,
  ImageBox,
  ImageText,
  ImgDiv,
  DetailTitle,
  CommentListDiv,
} from "./style";
import { useDispatch, useSelector } from "react-redux";
import { deleteComment, getComments } from "../../redux/commentsSlice";

const ContainerForm = () => {
  const comments = useSelector((state) => state.comments);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getComments());
  }, [dispatch]);

  const handlerDeleteComment = (id) => {
    dispatch(deleteComment(id));
  };

  return (
    <>
      <DetailContainer>
        <DetailBanner>Oh! Honey Scrooge</DetailBanner>
        <DetailTitle>상세 페이지</DetailTitle>
        <ImgDiv>
          <ImageBox>
            <p>꿀템 사진</p>
          </ImageBox>
          <ImageText>
            <p>꿀템 설명</p>
          </ImageText>
        </ImgDiv>
        <CommentDiv>
          <DetailComment>
            <div id="comment-count">댓글 숫자 0 </div>
            <input id="comment-input" placeholder="댓글을 입력하세요"></input>
            <button>확인</button>
          </DetailComment>
          <CommentListDiv>
            <div id="comments">
              {comments.map((comment) => (
                <div className="comment" key={comment.id}>
                  <div className="comment-user">
                    <p>{comment.user}</p>
                  </div>
                  <div className="comment-content">
                    <p>{comment.content}</p>
                  </div>
                  <div className="comment-date">
                    <p>{comment.date}</p>
                  </div>
                  <button className="comment-edit">Edit</button>
                  <button className="comment-delete" onClick={() => handlerDeleteComment(comment.id)}>
                    Delete
                  </button>
                </div>
              ))}
              ;
            </div>
          </CommentListDiv>
        </CommentDiv>
      </DetailContainer>
    </>
  );
};

export default ContainerForm;
